// Custom Scroll for Sidebar
$(function() {
	$('.sidebar-content').slimScroll({
		color: '#353535',
		alwaysVisible: false,
		size: "5px",
		height: "100%",
		distance: '1px',
		railVisible: false,
		railColor: "#353535",
		position: 'left',
	});
});


